/* Procedure structure for procedure `sp_edit_artikel` */

/*!50003 DROP PROCEDURE IF EXISTS  `sp_edit_artikel` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_edit_artikel`(in id int(3))
BEGIN
	select * from tb_artikel_berita where id_artikel_berita = id;
   END */$$
DELIMITER ;

/* Procedure structure for procedure `sp_edit_artikel_pilihan` */

/*!50003 DROP PROCEDURE IF EXISTS  `sp_edit_artikel_pilihan` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_edit_artikel_pilihan`(in pengarangA varchar(50), in judulA varchar(50), in isiA text, in id int(3))
BEGIN
	update tb_artikel_berita SET pengarang = pengarangA ,judul = judulA, isi = isiA, tanggal = current_date
	where id_artikel_berita=id;
    END */$$
DELIMITER ;

