/* Procedure structure for procedure `sp_masukkan_komentar` */

/*!50003 DROP PROCEDURE IF EXISTS  `sp_masukkan_komentar` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_masukkan_komentar`(in id int(3), in email1 varchar(50), in isi1 text, in flag1 int(1))
BEGIN
	declare id1 int(3);
	declare judul1 varchar(50);
	SET id1 = (select max(id_komentar) from tb_komentar)+1;
	set judul1 = (select distinct(judul) from tb_komentar where id_sub_komentar = id);
	insert into tb_komentar(id_komentar, id_sub_komentar, email, judul, isi, flag, tanggal) values(id1, id, email1, judul1, isi1, flag1, current_timestamp);
    END */$$
DELIMITER ;

/* Procedure structure for procedure `sp_masukkan_komentar_baru` */

/*!50003 DROP PROCEDURE IF EXISTS  `sp_masukkan_komentar_baru` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_masukkan_komentar_baru`(in email1 varchar(50), in judul1 varchar(50), in isi1 text, in flag1 int(1))
BEGIN
	declare id1 int(3);
	SET id1 = (select max(id_komentar) from tb_komentar)+1;
	insert into tb_komentar(id_komentar, id_sub_komentar, email, judul, isi, flag, tanggal) values(id1, 1, email1, judul1, isi1, flag1, current_timestamp);
    END */$$
DELIMITER ;

/* Procedure structure for procedure `sp_search_content` */

/*!50003 DROP PROCEDURE IF EXISTS  `sp_search_content` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_search_content`(in jenis varchar(50))
BEGIN
SELECT * FROM tb_artikel_berita where kategori = jenis;
    END */$$
DELIMITER ;

/* Procedure structure for procedure `sp_tambah_artikel` */

/*!50003 DROP PROCEDURE IF EXISTS  `sp_tambah_artikel` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_tambah_artikel`(in judul varchar(50), in pengarang varchar(50), in kategori varchar(255), in isi text)
BEGIN
	declare id int(2);
	set id = (select max(id_artikel_berita) from tb_artikel_berita)+1;
	insert into tb_artikel_berita values(id, judul, kategori, isi, current_date, pengarang);
    END */$$
DELIMITER ;

/* Procedure structure for procedure `sp_tambah_foto` */

/*!50003 DROP PROCEDURE IF EXISTS  `sp_tambah_foto` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_tambah_foto`(in judul varchar(50))
BEGIN
	declare id int(3);
	set id = (select max(id_foto) from tb_foto)+1;
	insert into tb_foto values(id, judul);
    END */$$
DELIMITER ;

/*!40101 SET SQL_MODE=@OLD_SQL_MODE */;
/*!40014 SET FOREIGN_KEY_CHECKS=@OLD_FOREIGN_KEY_CHECKS */;
